import axios from 'axios'

const API_URL = "localhost:4000/api"

export const login = async (username, password) => {
  console.log('inside actions:', username, password)
	try { 
	  const res = await axios.post(`${API_URL}/login`, {
	    username: username,
	    password: password
	  })

	  // localStorage.setItem('token', res.headers.authorization)
	} catch (error) {
	  // handle error
	  console.error(error)
    return error
	}
}

export const logout = async (username, password) => {
	try { 
	  const res = await axios.post(`${API_URL}/login`, {
	    username: username,
	    password: password
	  })

	  localStorage.logout('token', res.headers.authorization)
	} catch (error) {
	  // handle error
	  console.error(error)
    return error
	}
}
