import React, { useState,  useContext, useEffect } from 'react'
import { isInputEvent, setNestedObjectValues, useFormik } from 'formik'
import axios from 'axios'
// import { AuthContext } from '../../store/authContext'
// import { Redirect} from 'react-router-dom'
import { login, logout} from './actions'

import { useSelector, useDispatch } from 'react-redux'
import { loginR, logoutR } from './loginReducer'


const Login = () => {
  const user = useSelector((state) => state.auth.username)
  const dispatch = useDispatch()
  console.log(user)

  const [err, setErr] = useState(false)
  const formik = useFormik({
    initialValues: {
      username: '',
      password: '',
    },
    onSubmit: async ({username, password}) => {
      try { 
        const res = await axios.post(`${API_URL}/login`, {
          username: username,
          password: password
        })
        console.log('this shouldnt ')

        return res.data
        // localStorage.setItem('token', res.headers.authorization)
      } catch (error) {
        dispatch(loginR('no', 'no', false))
        // return {failed: true, error: error.message, reason: 'user doesnt exist'}
      }
    },
  });

  return (
    <div className="alpha signin-form">
      <h1>Welcome Fabric Risk</h1>
      {true ?
        <form onSubmit={formik.handleSubmit}  className="space">
          <input
            id="username"
            name="username"
            type="username"
            onChange={formik.handleChange}
            value={formik.values.username}
            placeholder="Username or Email"
            className="Input-text-search"
          />
          <br/>
          <input
            id="password"
            name="password"
            type="password"
            onChange={formik.handleChange}
            value={formik.values.password}
            placeholder="Password"
            className="Input-text-search"
          />
          <br/>
          <button type="submit" className="btn2 btn-light btn-lsm">Login</button>
        </form>
      : <div></div>
      // : <Redirect to="/" />
      }
    </div>
  );
}

