import { useState, useEffect } from "react";
import { BrowserRouter, Switch, Route } from "react-router-dom"
import Login from './login'
import { useAuth } from "./store/use-auth";

//====================================================
// these are assets i created with a personal project
// so variable name doesn't line up with login,
// just wanted to mention that so i dont look like 
// big dumb dumb doing .signup -> login comp
//====================================================
import './styles/App.css'
import './styles/main.css'

function App() {
  const [reload, setReload] = useState(false)
  const user = useAuth()

  console.log(user.isLoggedin())

  useEffect(() => {
    setReload(user.isLoggedin())
  },[user.isLoggedin()])

  return (
    <div className="App">
        <BrowserRouter>
        {!reload 
          ? <>
              <Login />
              <Switch>
                <Route path='/login' component={Login} />
              </Switch>
            </>
          : <>
              <Route exact path='/'> 
                <div className="alpha signin-form">
                  {`Username: ${user.getUser()}`}
                  <button 
                    type="submit" 
                    className="btn2 btn-light btn-lsm"
                    onClick={() => user.logout()}
                  >
                    logout
                  </button>
                </div>
              </Route>
            </>
        }
      </BrowserRouter>
    </div>
  );
}

export default App;
