import { useState, useEffect } from "react";
import { BrowserRouter, Switch, Route } from "react-router-dom"
import Login from './login'
import { useAuth } from "./store/use-auth";

//====================================================
// these are assets i created with a personal project
// so variable name doesn't line up with login,
// just wanted to mention that so i dont look like 
// big dumb dumb doing .signup -> login comp
//====================================================
import './styles/App.css'
import './styles/main.css'

function App() {
  const [reload, setReload] = useState(false)
  const user = useAuth()

  useEffect(() => {
    setReload(user.isLoggedin())
    // if (user.isLoggedin())  setReload(true)
    // else                    setReload(false)
  },[user.isLoggedin()])

  return (
    <div className="App">
        <BrowserRouter>
        {!reload ?
          <>
            <Login />
            <Switch>
              <Route path='/login' component={Login} />
            </Switch>
          </>
          : 
          <>
            <Route exact path='/'> 
              <div>home</div>
            </Route>
          </>
        }
      </BrowserRouter>
    </div>
  );
}

export default App;

