import React, { useState, useEffect, useContext, createContext } from "react";
import axios from 'axios'

const API_URL = "http://localhost:8080/api"

const authContext = createContext();

export function ProvideAuth({ children }) {
	const auth = useProvideAuth();
	return <authContext.Provider value={auth}>{children}</authContext.Provider>;
}


export const useAuth = () => { 
  return useContext(authContext); 
};


function useProvideAuth() {
  // const [isError, setIsError]   = useState(false)
  // const [message, setMessage]   = useState(false)
  // const [loggedIn, setLoggedIn] = useState(false)
  // const [user, setUser]         = useState('')
  // const [password, setPassword] = useState('')
  // const [auth, isAuth]          = useState(false)

  const u = {
    username: '',
    password: '',
    isLoggedin: false,
    isError: false
  }

  const login = async (username, password) => {
    try { 
      const res = await axios.post(`${API_URL}/login`, {
        username: username,
        password: password
      })

      u.username = res.data.username
      u.password = res.data.password
      u.isLoggedin = true
      u.message = res.data.success

    } catch (error) {
      console.error(error)

      u.username = res.data.username
      u.password = res.data.password
      u.isLoggedin = true
      u.message = res.data.success

      // setIsError(true)
      // isAuth(false)
      // setMessage('user doesnt exist')
      // setUser('')
    }
  };

  const logout = async () => {
      setIsError(false)
      isAuth(false)
      setMessage('')
      setUser('')
  };

  const isLoggedin  = () => { return auth }
  const getPassword = () => { return password }
  const getUser     = () => { return u.username }


  return { 
    isLoggedin,
    login,
    logout,
    getUser,
    getPassword
  }
}
