        // console.log(err)
        // values.password = ''
        // localStorage.removeItem('user')
        // localStorage.removeItem('token')
        // isAuth(false)
        // setWrongpass(true)
      }
    },
  });

  return (
    <div className="alpha signin-form">
      <h1>Welcome Emijig</h1>
      { wrongpass 
        ? <>wrong password</>
        : <></>
      }
      {!auth
      ?<form onSubmit={formik.handleSubmit}  className="space">
        <input
          id="username"
          name="username"
          type="username"
          onChange={formik.handleChange}
          value={formik.values.username}
          placeholder="Username or Email"
          className="Input-text-search"
        />
        <br/>
        <input
          id="password"
          name="password"
          type="password"
          onChange={formik.handleChange}
          value={formik.values.password}
          placeholder="Password"
          className="Input-text-search"
        />
        <br/>
        <button type="submit" className="btn2 btn-light btn-lsm">sign in</button>
      </form>
      : <div></div>
      // : <Redirect to="/" />
      }
    </div>
  );
}

